import React, {useState, useEffect} from 'react';

const Contact = () => {
  const [contact, setContact] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3020/api/contacts`)
    .then(respose => respose.json())
    .then(data => {
      setContact(data.map(sub => (
        <form onSubmit={this.handleSubmit}>
        <label htmlFor="name">Enter name</label>
        <input id="name" name="name" type="text" onChange={this.handleChange} />

        <label htmlFor="comment">Enter comment</label>
        <input id="comment" name="comment" type="text" onChange={this.handleChange} />

        <label htmlFor="email">Enter email</label>
        <input id="email" name="email" type="email" onChange={this.handleChange} />

        <button>Send</button>
      </form>
      )));
    });
  },[])

   handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
