import React, { useState, useEffect } from 'react';

const Contact = () => {
  const [name, setName] = useState([]);
  const [comment, setComment] = useState([]);
  const [email, setEmail] = useState([]);

  const addComment = event => {
    console.log('hello');
    console.log(comment);
    console.log(name);
    console.log(email);

    fetch(`http://localhost:3020/api/contacts/create/${comment}/${email}/${name}`, { method: 'POST' })
      .then(respose => respose.json())
      .then(data => {
        console.log(data);
      });
    window.location.reload();
    event.preventDefault()
  }
  const [cont, setCont] = useState('')
  return (
    <div className="white-box">
    <form>
      <div className="container">
      <div className="row">
      <div className="col-lg-12">
      <label htmlFor="name">Enter name</label>
      <input id="name" name="name" type="text" onChange={event => setName(event.target.value)} />
</div>
</div>
<div className="row">
<div className="col-lg-12">
      <label htmlFor="comment">Enter comment</label>
      <input id="comment" name="comment" type="text" onChange={event => setComment(event.target.value)} />
</div>
</div>
<div className="row">
<div className="col-lg-12">
      <label htmlFor="email">Enter email</label>
      <input id="email" name="email" type="email" onChange={event => setEmail(event.target.value)} />
</div>
</div>
</div>
      <button onClick={addComment}>Send</button>
    </form>
    </div>
  );
}

export default Contact;
